class Clock{constructor({template:template}){this.template=template}render(){let date=new Date,hours=date.getHours();hours<10&&(hours="0"+hours);let mins=date.getMinutes();mins<10&&(mins="0"+mins);let secs=date.getSeconds();secs<10&&(secs="0"+secs);let output=this.template.replace("h",hours).replace("m",mins).replace("s",secs);console.log(output)}stop(){clearInterval(this.timer)}start(){this.render(),this.timer=setInterval(()=>this.render(),1e3)}}let clock=new Clock({template:"h:m:s"});clock.start();